import React, { createContext, useContext, useState, useEffect } from 'react'
import { Palette, PaletteId, PALETTES, DEFAULT_PALETTE_ID } from './palettes'

interface ThemeContextValue {
  palette: Palette
  paletteId: PaletteId
  setPaletteId: (id: PaletteId) => void
}

const ThemeContext = createContext<ThemeContextValue | null>(null)

function getInitialPaletteId(): PaletteId {
  try {
    const stored = localStorage.getItem('2048-palette')
    if (stored && stored in PALETTES) {
      return stored as PaletteId
    }
  } catch {
    // localStorage unavailable
  }
  return DEFAULT_PALETTE_ID
}

export const ThemeProvider: React.FC<{ children: React.ReactNode }> = ({ children }) => {
  const [paletteId, setPaletteId] = useState<PaletteId>(getInitialPaletteId)

  useEffect(() => {
    try {
      localStorage.setItem('2048-palette', paletteId)
    } catch {
      // localStorage unavailable
    }
  }, [paletteId])

  const value: ThemeContextValue = {
    palette: PALETTES[paletteId],
    paletteId,
    setPaletteId
  }

  return <ThemeContext.Provider value={value}>{children}</ThemeContext.Provider>
}

export function useTheme(): ThemeContextValue {
  const ctx = useContext(ThemeContext)
  if (!ctx) {
    throw new Error('useTheme must be used within a ThemeProvider')
  }
  return ctx
}

import React from 'react'
import { useTheme } from '../themes/ThemeContext'
import { PALETTES, PaletteId } from '../themes/palettes'

const paletteIds = Object.keys(PALETTES) as PaletteId[]

export const PaletteSelector: React.FC = () => {
  const { palette, paletteId, setPaletteId } = useTheme()

  return (
    <div
      style={{
        display: 'flex',
        gap: '8px',
        marginBottom: '16px',
        flexWrap: 'wrap',
        justifyContent: 'center'
      }}
    >
      {paletteIds.map((id) => {
        const p = PALETTES[id]
        const isActive = id === paletteId
        return (
          <button
            key={id}
            onClick={() => setPaletteId(id)}
            data-palette={id}
            data-active={isActive}
            style={{
              display: 'flex',
              alignItems: 'center',
              gap: '6px',
              padding: '6px 12px',
              border: isActive ? `2px solid ${palette.textDark}` : '2px solid transparent',
              borderRadius: '4px',
              backgroundColor: isActive ? palette.board : 'transparent',
              color: isActive ? palette.buttonText : palette.textDark,
              cursor: 'pointer',
              fontSize: '13px',
              fontWeight: isActive ? 'bold' : 'normal',
              fontFamily: 'Arial, sans-serif'
            }}
          >
            <span
              style={{
                display: 'inline-block',
                width: '14px',
                height: '14px',
                borderRadius: '50%',
                backgroundColor: p.board,
                border: '1px solid rgba(0,0,0,0.2)',
                flexShrink: 0
              }}
            />
            {p.name}
          </button>
        )
      })}
    </div>
  )
}
