      for (const div of allDivs) {
        const style = window.getComputedStyle(div);
        if (style.display === 'grid') {
          const r = div.getBoundingClientRect();
          checks.push({ name: 'board', inView: r.bottom <= vh && r.top >= 0 });
          break;
        }
      }
      // Score
      const scoreEl = [...document.querySelectorAll('p')].find(p => p.textContent === 'Score');
