    test_name: 'Tile Size Stability',
    status: results.passed ? 'passed' : 'failed',
    screenshots: [
      `${SCREENSHOT_DIR}/01_initial_board.png`,
      `${SCREENSHOT_DIR}/02_measured_dimensions.png`,
      `${SCREENSHOT_DIR}/03_after_first_move.png`,
