import React, { useState, useEffect } from 'react'
import { initializeGame, move, GameState } from '../game/logic'
import { Tile } from './Tile'

export const Game: React.FC = () => {
  const [state, setState] = useState<GameState>(initializeGame)

  useEffect(() => {
    const handleKeyPress = (e: KeyboardEvent) => {
      if (['ArrowUp', 'ArrowDown', 'ArrowLeft', 'ArrowRight'].includes(e.key)) {
        e.preventDefault()
        const directionMap: Record<string, 'left' | 'right' | 'up' | 'down'> = {
          ArrowLeft: 'left',
          ArrowRight: 'right',
          ArrowUp: 'up',
          ArrowDown: 'down'
        }
        setState((prev) => move(directionMap[e.key], prev))
      }
    }

    window.addEventListener('keydown', handleKeyPress)
    return () => window.removeEventListener('keydown', handleKeyPress)
  }, [])

  const handleNewGame = () => {
    setState(initializeGame())
  }

  return (
    <div
      style={{
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        justifyContent: 'center',
        minHeight: '100vh',
        backgroundColor: '#faf8f3',
        padding: '20px'
      }}
    >
      <h1 style={{ marginBottom: '20px', color: '#776e65' }}>2048</h1>

      <div style={{ marginBottom: '20px', display: 'flex', gap: '40px', alignItems: 'center' }}>
        <div>
          <p style={{ fontSize: '14px', color: '#9f8f7f', margin: 0 }}>Score</p>
          <p style={{ fontSize: '24px', fontWeight: 'bold', color: '#776e65', margin: '5px 0 0 0' }}>
            {state.score}
          </p>
        </div>
        <button
          onClick={handleNewGame}
          style={{
            padding: '10px 20px',
            backgroundColor: '#8f7a66',
            color: '#f9f6f2',
            border: 'none',
            borderRadius: '3px',
            fontSize: '16px',
            cursor: 'pointer'
          }}
        >
          New Game
        </button>
      </div>

      <div
        style={{
          display: 'grid',
          gridTemplateColumns: 'repeat(4, 1fr)',
          gap: '10px',
          backgroundColor: '#bbada0',
          padding: '10px',
          borderRadius: '3px',
          width: '340px',
          height: '340px'
        }}
      >
        {state.grid.map((row, i) =>
          row.map((value, j) => (
            <Tile key={`${i}-${j}`} value={value} />
          ))
        )}
      </div>

      {state.won && (
        <div
          style={{
            marginTop: '20px',
            padding: '20px',
            backgroundColor: '#edc22e',
            color: '#776e65',
            borderRadius: '3px',
            fontSize: '18px',
            fontWeight: 'bold'
          }}
        >
          🎉 You won! Keep going or start a new game.
        </div>
      )}

      {state.gameOver && (
        <div
          style={{
            marginTop: '20px',
            padding: '20px',
            backgroundColor: '#f67c5f',
            color: '#f9f6f2',
            borderRadius: '3px',
            fontSize: '18px',
            fontWeight: 'bold'
          }}
        >
          Game Over! Start a new game to play again.
        </div>
      )}

      <div style={{ marginTop: '40px', textAlign: 'center', color: '#9f8f7f', fontSize: '14px' }}>
        <p>Use arrow keys to move tiles</p>
        <p>When two tiles with the same number touch, they merge!</p>
      </div>
    </div>
  )
}

import React from 'react'

interface TileProps {
  value: number | null
}

export const Tile: React.FC<TileProps> = ({ value }) => {
  const getBackgroundColor = (val: number | null): string => {
    const colors: Record<number, string> = {
      2: '#eee4da',
      4: '#ede0c8',
      8: '#f2b179',
      16: '#f59563',
      32: '#f67c5f',
      64: '#f65e3b',
      128: '#edcf72',
      256: '#edcc61',
      512: '#edc850',
      1024: '#edc53f',
      2048: '#edc22e'
    }
    return colors[val!] || '#cdc1b4'
  }

  const getTextColor = (val: number | null): string => {
    return val === null || val === 2 || val === 4 ? '#776e65' : '#f9f6f2'
  }

  return (
    <div
      style={{
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
        backgroundColor: getBackgroundColor(value),
        color: getTextColor(value),
        fontSize: value! > 1024 ? '30px' : '40px',
        fontWeight: 'bold',
        borderRadius: '3px',
        width: '100%',
        height: '100%',
        transition: 'all 0.15s ease-in-out'
      }}
    >
      {value}
    </div>
  )
}

import React from 'react'
import { Game } from './components/Game'

export const App: React.FC = () => {
  return (
    <div style={{ fontFamily: 'Arial, sans-serif' }}>
      <Game />
    </div>
  )
}

export default App

export type Grid = (number | null)[][]

export interface GameState {
  grid: Grid
  score: number
  moves: number
  gameOver: boolean
  won: boolean
}

export function createEmptyGrid(): Grid {
  return Array(4)
    .fill(null)
    .map(() => Array(4).fill(null))
}

export function initializeGame(): GameState {
  const grid = createEmptyGrid()
  addNewTile(grid)
  addNewTile(grid)

  return {
    grid,
    score: 0,
    moves: 0,
    gameOver: false,
    won: false
  }
}

export function addNewTile(grid: Grid): void {
  const empty = getEmptyTiles(grid)
  if (empty.length === 0) return

  const [row, col] = empty[Math.floor(Math.random() * empty.length)]
  grid[row][col] = Math.random() < 0.9 ? 2 : 4
}

export function getEmptyTiles(grid: Grid): [number, number][] {
  const empty: [number, number][] = []
  for (let i = 0; i < 4; i++) {
    for (let j = 0; j < 4; j++) {
      if (grid[i][j] === null) {
        empty.push([i, j])
      }
    }
  }
  return empty
}

function slide(row: (number | null)[]): { row: (number | null)[]; score: number } {
  let score = 0
  const result = row.filter((val) => val !== null) as number[]

  for (let i = 0; i < result.length - 1; i++) {
    if (result[i] === result[i + 1]) {
      result[i] *= 2
      score += result[i]
      result.splice(i + 1, 1)
    }
  }

  while (result.length < 4) {
    result.push(null as any)
  }

  return { row: result, score }
}

export function moveLeft(state: GameState): GameState {
  const newGrid = state.grid.map((row) => [...row])
  let score = 0

  for (let i = 0; i < 4; i++) {
    const { row, score: rowScore } = slide(newGrid[i])
    newGrid[i] = row
    score += rowScore
  }

  return { ...state, grid: newGrid, score: state.score + score }
}

export function moveRight(state: GameState): GameState {
  const newGrid = state.grid.map((row) => [...row].reverse())
  let score = 0

  for (let i = 0; i < 4; i++) {
    const { row, score: rowScore } = slide(newGrid[i])
    newGrid[i] = row.reverse()
    score += rowScore
  }

  return { ...state, grid: newGrid, score: state.score + score }
}

export function moveUp(state: GameState): GameState {
  const newGrid = createEmptyGrid()
  let score = 0

  for (let j = 0; j < 4; j++) {
    const column = [
      state.grid[0][j],
      state.grid[1][j],
      state.grid[2][j],
      state.grid[3][j]
    ]
    const { row, score: colScore } = slide(column)
    score += colScore

    for (let i = 0; i < 4; i++) {
      newGrid[i][j] = row[i]
    }
  }

  return { ...state, grid: newGrid, score: state.score + score }
}

export function moveDown(state: GameState): GameState {
  const newGrid = createEmptyGrid()
  let score = 0

  for (let j = 0; j < 4; j++) {
    const column = [
      state.grid[3][j],
      state.grid[2][j],
      state.grid[1][j],
      state.grid[0][j]
    ]
    const { row, score: colScore } = slide(column)
    score += colScore

    for (let i = 0; i < 4; i++) {
      newGrid[3 - i][j] = row[i]
    }
  }

  return { ...state, grid: newGrid, score: state.score + score }
}

export function isGridEqual(grid1: Grid, grid2: Grid): boolean {
  for (let i = 0; i < 4; i++) {
    for (let j = 0; j < 4; j++) {
      if (grid1[i][j] !== grid2[i][j]) return false
    }
  }
  return true
}

export function canMove(state: GameState): boolean {
  const directions = [
    moveLeft(state),
    moveRight(state),
    moveUp(state),
    moveDown(state)
  ]

  return directions.some((dir) => !isGridEqual(dir.grid, state.grid))
}

export function move(direction: 'left' | 'right' | 'up' | 'down', state: GameState): GameState {
  if (state.gameOver || state.won) return state

  let newState: GameState

  switch (direction) {
    case 'left':
      newState = moveLeft(state)
      break
    case 'right':
      newState = moveRight(state)
      break
    case 'up':
      newState = moveUp(state)
      break
    case 'down':
      newState = moveDown(state)
      break
  }

  if (isGridEqual(newState.grid, state.grid)) {
    return state
  }

  addNewTile(newState.grid)
  newState.moves += 1

  // Check for win
  if (!state.won) {
    for (let i = 0; i < 4; i++) {
      for (let j = 0; j < 4; j++) {
        if (newState.grid[i][j] === 2048) {
          newState.won = true
          break
        }
      }
    }
  }

  // Check for game over
  if (!canMove(newState)) {
    newState.gameOver = true
  }

  return newState
}

