import { chromium } from 'playwright';

const SCREENSHOT_DIR = '/home/luciano/agentic/agents/6ffc5aae/sdlc_implementor/img/tile_size_stability';
const APP_URL = 'http://localhost:5173';

async function run() {
  const browser = await chromium.launch({ headless: true });
  const page = await browser.newPage({ viewport: { width: 1280, height: 720 } });
  const results = { steps: [], passed: true };

  function log(step, msg, ok) {
    results.steps.push({ step, msg, ok });
    if (!ok) results.passed = false;
    console.log(`(Step ${step} ${ok ? '✅' : '❌'}) ${msg}`);
  }

  try {
    // Step 1: Navigate
    await page.goto(APP_URL, { waitUntil: 'networkidle' });
    log(1, 'Navigated to application', true);

    // Step 2: Screenshot initial board
    await page.screenshot({ path: `${SCREENSHOT_DIR}/01_initial_board.png`, fullPage: true });
    log(2, 'Took screenshot of initial board state', true);

    // Step 3: Verify title
    const title = await page.locator('h1').textContent();
    log(3, `Page title heading: "${title}"`, title?.trim() === '2048');

    // Step 4: Verify core UI elements
    const tiles = page.locator('div[style*="display: grid"] > div');
    const tileCount = await tiles.count();
    log('4a', `Game board has ${tileCount} tile cells (expected 16)`, tileCount === 16);

    const scoreText = await page.locator('text=Score').isVisible();
    log('4b', `Score display visible: ${scoreText}`, scoreText);

    const newGameBtn = await page.locator('button:has-text("New Game")').isVisible();
    log('4c', `New Game button visible: ${newGameBtn}`, newGameBtn);

    const instructions = await page.locator('text=Use arrow keys to move tiles').isVisible();
    log('4d', `Instruction text visible: ${instructions}`, instructions);

    // Step 5-7: Measure tile dimensions
    const initialDims = await tiles.evaluateAll(els =>
      els.map(el => {
        const rect = el.getBoundingClientRect();
        return { width: Math.round(rect.width), height: Math.round(rect.height) };
      })
    );

    const allWidth75 = initialDims.every(d => d.width === 75);
    const allHeight75 = initialDims.every(d => d.height === 75);
    log(5, `Measured ${initialDims.length} tiles: widths=${[...new Set(initialDims.map(d => d.width))].join(',')}, heights=${[...new Set(initialDims.map(d => d.height))].join(',')}`, true);
    log(6, `All tile cells width 75px: ${allWidth75}`, allWidth75);
    log(7, `All tile cells height 75px: ${allHeight75}`, allHeight75);

    // Step 8: Screenshot measured dimensions
    await page.screenshot({ path: `${SCREENSHOT_DIR}/02_measured_dimensions.png`, fullPage: true });
    log(8, 'Took screenshot showing measured tile dimensions', true);

    // Step 9: Press ArrowRight
    await page.keyboard.press('ArrowRight');
    log(9, 'Pressed ArrowRight key', true);

    // Step 10: Wait 300ms
    await page.waitForTimeout(300);
    log(10, 'Waited 300ms for transitions', true);

    // Step 11-12: Measure after first move
    const afterRightDims = await tiles.evaluateAll(els =>
      els.map(el => {
        const rect = el.getBoundingClientRect();
        return { width: Math.round(rect.width), height: Math.round(rect.height) };
      })
    );

    const dimsMatchAfterRight = afterRightDims.every(d => d.width === 75 && d.height === 75);
    log(11, `Post-move 1 widths=${[...new Set(afterRightDims.map(d => d.width))].join(',')}, heights=${[...new Set(afterRightDims.map(d => d.height))].join(',')}`, true);
    log(12, `Tile dimensions unchanged after ArrowRight: ${dimsMatchAfterRight}`, dimsMatchAfterRight);

    // Step 13: Screenshot after first move
    await page.screenshot({ path: `${SCREENSHOT_DIR}/03_after_first_move.png`, fullPage: true });
    log(13, 'Took screenshot after first move', true);

    // Step 14: Press ArrowDown
    await page.keyboard.press('ArrowDown');
    log(14, 'Pressed ArrowDown key', true);

    // Step 15: Wait 300ms
    await page.waitForTimeout(300);
    log(15, 'Waited 300ms for transitions', true);

    // Step 16-17: Measure after second move
    const afterDownDims = await tiles.evaluateAll(els =>
      els.map(el => {
        const rect = el.getBoundingClientRect();
        return { width: Math.round(rect.width), height: Math.round(rect.height) };
      })
    );

    const dimsMatchAfterDown = afterDownDims.every(d => d.width === 75 && d.height === 75);
    log(16, `Post-move 2 widths=${[...new Set(afterDownDims.map(d => d.width))].join(',')}, heights=${[...new Set(afterDownDims.map(d => d.height))].join(',')}`, true);
    log(17, `Tile dimensions unchanged after ArrowDown: ${dimsMatchAfterDown}`, dimsMatchAfterDown);

    // Step 18: Screenshot after second move
    await page.screenshot({ path: `${SCREENSHOT_DIR}/04_after_second_move.png`, fullPage: true });
    log(18, 'Took screenshot after second move', true);

    // Step 19: Verify all elements within viewport
    const viewportHeight = 720;
    const viewportWidth = 1280;

    const elementsInViewport = await page.evaluate((vh) => {
      const checks = [];
      // Board
      const grid = document.querySelector('div[style*="display: grid"]');
      if (grid) {
        const r = grid.getBoundingClientRect();
        checks.push({ name: 'board', inView: r.bottom <= vh && r.top >= 0 });
      }
      // Score
      const scoreEl = [...document.querySelectorAll('p')].find(p => p.textContent === 'Score');
      if (scoreEl) {
        const r = scoreEl.getBoundingClientRect();
        checks.push({ name: 'score', inView: r.bottom <= vh && r.top >= 0 });
      }
      // New Game button
      const btn = document.querySelector('button');
      if (btn) {
        const r = btn.getBoundingClientRect();
        checks.push({ name: 'newGame', inView: r.bottom <= vh && r.top >= 0 });
      }
      // Instructions
      const instrEl = [...document.querySelectorAll('p')].find(p => p.textContent?.includes('arrow keys'));
      if (instrEl) {
        const r = instrEl.getBoundingClientRect();
        checks.push({ name: 'instructions', inView: r.bottom <= vh && r.top >= 0 });
      }
      return checks;
    }, viewportHeight);

    const allInViewport = elementsInViewport.every(e => e.inView);
    log(19, `All elements in viewport: ${JSON.stringify(elementsInViewport)}`, allInViewport);

    // Step 20: Full page screenshot
    await page.screenshot({ path: `${SCREENSHOT_DIR}/05_full_page_visible.png`, fullPage: true });
    log(20, 'Took full page screenshot showing all elements visible', true);

  } catch (err) {
    console.error('Test error:', err.message);
    results.passed = false;
  } finally {
    await browser.close();
  }

  // Output JSON result
  const output = {
    test_name: 'Tile Size Stability',
    status: results.passed ? 'passed' : 'failed',
    screenshots: [
      `${SCREENSHOT_DIR}/01_initial_board.png`,
      `${SCREENSHOT_DIR}/02_measured_dimensions.png`,
      `${SCREENSHOT_DIR}/03_after_first_move.png`,
      `${SCREENSHOT_DIR}/04_after_second_move.png`,
      `${SCREENSHOT_DIR}/05_full_page_visible.png`
    ],
    error: results.passed ? null : results.steps.filter(s => !s.ok).map(s => s.msg).join('; ')
  };

  console.log('\n--- TEST RESULT ---');
  console.log(JSON.stringify(output, null, 2));
}

run();

