import { chromium } from 'playwright';

const SCREENSHOT_DIR = '/home/luciano/agentic/agents/1be417b6/sdlc_implementor/img/tile_size_stability';
const APP_URL = 'http://localhost:5173';

const results = {
  test_name: 'Tile Size Stability',
  status: 'passed',
  screenshots: [],
  error: null,
  steps: []
};

function log(step, msg) {
  console.log(`(Step ${step}) ${msg}`);
  results.steps.push(`(Step ${step}) ${msg}`);
}

function fail(step, msg) {
  const fullMsg = `(Step ${step} ❌) ${msg}`;
  console.error(fullMsg);
  results.status = 'failed';
  results.error = fullMsg;
  results.steps.push(fullMsg);
}

async function run() {
  const browser = await chromium.launch({ headless: true });
  const context = await browser.newContext({ viewport: { width: 1280, height: 800 } });
  const page = await context.newPage();

  try {
    // Step 1: Navigate
    await page.goto(APP_URL, { waitUntil: 'networkidle' });
    log(1, '✅ Navigated to ' + APP_URL);

    // Step 2: Screenshot initial board state
    const ss1 = `${SCREENSHOT_DIR}/01_initial_board_state.png`;
    await page.screenshot({ path: ss1, fullPage: false });
    results.screenshots.push(ss1);
    log(2, '✅ Screenshot taken: initial board state');

    // Step 3: Verify heading "2048"
    const heading = await page.locator('h1').textContent();
    if (!heading || !heading.includes('2048')) {
      fail(3, `Heading "2048" not found, got: "${heading}"`);
      return results;
    }
    log(3, '✅ Heading "2048" is visible');

    // Step 4: Verify core UI elements
    // Grid with 16 tile cells
    const gridContainer = page.locator('div[style*="grid"]');
    const tileCells = gridContainer.locator('> div');
    const tileCount = await tileCells.count();
    if (tileCount !== 16) {
      fail(4, `Expected 16 tile cells, found ${tileCount}`);
      return results;
    }
    log(4, '✅ Game board has 16 tile cells');

    // Score display
    const scoreText = await page.locator('text=Score').count();
    if (scoreText < 1) {
      fail(4, 'Score display not found');
      return results;
    }
    log(4, '✅ Score display present');

    // New Game button
    const newGameBtn = page.locator('button:has-text("New Game")');
    if (await newGameBtn.count() < 1) {
      fail(4, 'New Game button not found');
      return results;
    }
    log(4, '✅ New Game button present');

    // Instruction text
    const instructionText = await page.locator('text=Use arrow keys to move tiles').count();
    if (instructionText < 1) {
      fail(4, 'Instruction text not found');
      return results;
    }
    log(4, '✅ Instruction text present');

    // Step 5 & 6 & 7: Measure all tile dimensions
    const initialDimensions = await tileCells.evaluateAll(tiles =>
      tiles.map(t => {
        const rect = t.getBoundingClientRect();
        return { width: Math.round(rect.width), height: Math.round(rect.height) };
      })
    );
    log(5, `✅ Measured ${initialDimensions.length} tile dimensions`);

    const allWidth75 = initialDimensions.every(d => d.width === 75);
    const allHeight75 = initialDimensions.every(d => d.height === 75);

    if (!allWidth75) {
      const widths = initialDimensions.map(d => d.width);
      fail(6, `Not all tiles have width 75px. Widths: ${JSON.stringify(widths)}`);
      return results;
    }
    log(6, '✅ All tile cells have identical width: 75px');

    if (!allHeight75) {
      const heights = initialDimensions.map(d => d.height);
      fail(7, `Not all tiles have height 75px. Heights: ${JSON.stringify(heights)}`);
      return results;
    }
    log(7, '✅ All tile cells have identical height: 75px');

    // Step 8: Screenshot showing measured dimensions
    const ss2 = `${SCREENSHOT_DIR}/02_measured_tile_dimensions.png`;
    await page.screenshot({ path: ss2, fullPage: false });
    results.screenshots.push(ss2);
    log(8, '✅ Screenshot taken: measured tile dimensions');

    // Step 9: Press ArrowRight
    await page.keyboard.press('ArrowRight');
    log(9, '✅ Pressed ArrowRight key');

    // Step 10: Wait 300ms
    await page.waitForTimeout(300);
    log(10, '✅ Waited 300ms for transitions');

    // Step 11 & 12: Measure after first move
    const afterMove1 = await tileCells.evaluateAll(tiles =>
      tiles.map(t => {
        const rect = t.getBoundingClientRect();
        return { width: Math.round(rect.width), height: Math.round(rect.height) };
      })
    );
    log(11, `✅ Measured ${afterMove1.length} tile dimensions after first move`);

    const move1Match = afterMove1.every((d, i) =>
      d.width === initialDimensions[i].width && d.height === initialDimensions[i].height
    );
    if (!move1Match) {
      fail(12, `Tile dimensions changed after first move. Before: ${JSON.stringify(initialDimensions[0])}, After: ${JSON.stringify(afterMove1[0])}`);
      return results;
    }
    log(12, '✅ All tile dimensions match after first move (no resizing)');

    // Step 13: Screenshot after first move
    const ss3 = `${SCREENSHOT_DIR}/03_after_first_move.png`;
    await page.screenshot({ path: ss3, fullPage: false });
    results.screenshots.push(ss3);
    log(13, '✅ Screenshot taken: after first move');

    // Step 14: Press ArrowDown
    await page.keyboard.press('ArrowDown');
    log(14, '✅ Pressed ArrowDown key');

    // Step 15: Wait 300ms
    await page.waitForTimeout(300);
    log(15, '✅ Waited 300ms for transitions');

    // Step 16 & 17: Measure after second move
    const afterMove2 = await tileCells.evaluateAll(tiles =>
      tiles.map(t => {
        const rect = t.getBoundingClientRect();
        return { width: Math.round(rect.width), height: Math.round(rect.height) };
      })
    );
    log(16, `✅ Measured ${afterMove2.length} tile dimensions after second move`);

    const move2Match = afterMove2.every((d, i) =>
      d.width === initialDimensions[i].width && d.height === initialDimensions[i].height
    );
    if (!move2Match) {
      fail(17, `Tile dimensions changed after second move. Before: ${JSON.stringify(initialDimensions[0])}, After: ${JSON.stringify(afterMove2[0])}`);
      return results;
    }
    log(17, '✅ All tile dimensions match after second move (no resizing)');

    // Step 18: Screenshot after second move
    const ss4 = `${SCREENSHOT_DIR}/04_after_second_move.png`;
    await page.screenshot({ path: ss4, fullPage: false });
    results.screenshots.push(ss4);
    log(18, '✅ Screenshot taken: after second move');

    // Step 19: Verify all elements visible without scrolling
    const viewportHeight = 800;
    const bodyHeight = await page.evaluate(() => document.body.scrollHeight);
    const allVisible = bodyHeight <= viewportHeight + 10; // small tolerance

    if (!allVisible) {
      // Check specific elements are within viewport
      const elementsInViewport = await page.evaluate((vh) => {
        const grid = document.querySelector('div[style*="grid"]');
        const score = document.querySelector('p');
        const button = document.querySelector('button');
        const checks = [];
        if (grid) {
          const r = grid.getBoundingClientRect();
          checks.push({ el: 'grid', bottom: r.bottom, inViewport: r.bottom <= vh });
        }
        if (button) {
          const r = button.getBoundingClientRect();
          checks.push({ el: 'button', bottom: r.bottom, inViewport: r.bottom <= vh });
        }
        return checks;
      }, viewportHeight);

      const allElementsInView = elementsInViewport.every(e => e.inViewport);
      if (!allElementsInView) {
        fail(19, `Not all elements visible in viewport. Details: ${JSON.stringify(elementsInViewport)}`);
        return results;
      }
    }
    log(19, '✅ Board and controls are fully visible within viewport');

    // Step 20: Full page screenshot
    const ss5 = `${SCREENSHOT_DIR}/05_full_page_visible.png`;
    await page.screenshot({ path: ss5, fullPage: true });
    results.screenshots.push(ss5);
    log(20, '✅ Screenshot taken: full page showing all elements visible');

  } catch (err) {
    results.status = 'failed';
    results.error = err.message;
    console.error('Test error:', err.message);
  } finally {
    await browser.close();
  }

  return results;
}

run().then(r => {
  console.log('\n--- TEST RESULTS ---');
  console.log(JSON.stringify(r, null, 2));
});

