import React from 'react'
import { Game } from './components/Game'
import { ThemeProvider } from './themes/ThemeContext'

export const App: React.FC = () => {
  return (
    <ThemeProvider>
      <div style={{ fontFamily: 'Arial, sans-serif' }}>
        <Game />
      </div>
    </ThemeProvider>
  )
}

export default App

import React, { useState, useEffect } from 'react'
import { initializeGame, move, GameState } from '../game/logic'
import { Tile } from './Tile'
import { PaletteSelector } from './PaletteSelector'
import { useTheme } from '../themes/ThemeContext'

export const Game: React.FC = () => {
  const [state, setState] = useState<GameState>(initializeGame)
  const { palette } = useTheme()

  useEffect(() => {
    const handleKeyPress = (e: KeyboardEvent) => {
      if (['ArrowUp', 'ArrowDown', 'ArrowLeft', 'ArrowRight'].includes(e.key)) {
        e.preventDefault()
        const directionMap: Record<string, 'left' | 'right' | 'up' | 'down'> = {
          ArrowLeft: 'left',
          ArrowRight: 'right',
          ArrowUp: 'up',
          ArrowDown: 'down'
        }
        setState((prev) => move(directionMap[e.key], prev))
      }
    }

    window.addEventListener('keydown', handleKeyPress)
    return () => window.removeEventListener('keydown', handleKeyPress)
  }, [])

  const handleNewGame = () => {
    setState(initializeGame())
  }

  return (
    <div
      style={{
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        justifyContent: 'center',
        minHeight: '100vh',
        backgroundColor: palette.background,
        padding: '20px'
      }}
    >
      <h1 style={{ marginBottom: '20px', color: palette.textDark }}>2048</h1>

      <PaletteSelector />

      <div style={{ marginBottom: '20px', display: 'flex', gap: '40px', alignItems: 'center' }}>
        <div>
          <p style={{ fontSize: '14px', color: palette.scoreLabel, margin: 0 }}>Score</p>
          <p style={{ fontSize: '24px', fontWeight: 'bold', color: palette.textDark, margin: '5px 0 0 0' }}>
            {state.score}
          </p>
        </div>
        <button
          onClick={handleNewGame}
          style={{
            padding: '10px 20px',
            backgroundColor: palette.buttonBg,
            color: palette.buttonText,
            border: 'none',
            borderRadius: '3px',
            fontSize: '16px',
            cursor: 'pointer'
          }}
        >
          New Game
        </button>
      </div>

      <div
        style={{
          display: 'grid',
          gridTemplateColumns: 'repeat(4, 75px)',
          gridTemplateRows: 'repeat(4, 75px)',
          gap: '10px',
          backgroundColor: palette.board,
          padding: '10px',
          borderRadius: '3px',
          width: 'fit-content',
          height: 'fit-content',
          maxWidth: '100%',
          boxSizing: 'border-box' as const,
          overflow: 'hidden'
        }}
      >
        {state.grid.map((row, i) =>
          row.map((value, j) => (
            <Tile key={`${i}-${j}`} value={value} />
          ))
        )}
      </div>

      {state.won && (
        <div
          style={{
            marginTop: '20px',
            padding: '20px',
            backgroundColor: palette.winBg,
            color: palette.winText,
            borderRadius: '3px',
            fontSize: '18px',
            fontWeight: 'bold'
          }}
        >
          🎉 You won! Keep going or start a new game.
        </div>
      )}

      {state.gameOver && (
        <div
          style={{
            marginTop: '20px',
            padding: '20px',
            backgroundColor: palette.gameOverBg,
            color: palette.gameOverText,
            borderRadius: '3px',
            fontSize: '18px',
            fontWeight: 'bold'
          }}
        >
          Game Over! Start a new game to play again.
        </div>
      )}

      <div style={{ marginTop: '40px', textAlign: 'center', color: palette.scoreLabel, fontSize: '14px' }}>
        <p>Use arrow keys to move tiles</p>
        <p>When two tiles with the same number touch, they merge!</p>
      </div>
    </div>
  )
}

import React from 'react'
import { useTheme } from '../themes/ThemeContext'

interface TileProps {
  value: number | null
}

export const Tile: React.FC<TileProps> = ({ value }) => {
  const { palette } = useTheme()

  const getBackgroundColor = (val: number | null): string => {
    if (val === null) return palette.emptyCell
    return palette.tileColors[val] || palette.emptyCell
  }

  const getTextColor = (val: number | null): string => {
    return val === null || val === 2 || val === 4 ? palette.textDark : palette.textLight
  }

  return (
    <div
      style={{
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
        backgroundColor: getBackgroundColor(value),
        color: getTextColor(value),
        fontSize: value! > 1024 ? '30px' : '40px',
        fontWeight: 'bold',
        borderRadius: '3px',
        width: '75px',
        height: '75px',
        transition: 'background-color 0.15s ease-in-out, color 0.15s ease-in-out'
      }}
    >
      {value}
    </div>
  )
}

import React from 'react'
import { useTheme } from '../themes/ThemeContext'
import { PALETTES, PaletteId } from '../themes/palettes'

const paletteIds = Object.keys(PALETTES) as PaletteId[]

export const PaletteSelector: React.FC = () => {
  const { palette, paletteId, setPaletteId } = useTheme()

  return (
    <div
      style={{
        display: 'flex',
        gap: '8px',
        marginBottom: '16px',
        flexWrap: 'wrap',
        justifyContent: 'center'
      }}
    >
      {paletteIds.map((id) => {
        const p = PALETTES[id]
        const isActive = id === paletteId
        return (
          <button
            key={id}
            onClick={() => setPaletteId(id)}
            data-palette={id}
            data-active={isActive}
            style={{
              display: 'flex',
              alignItems: 'center',
              gap: '6px',
              padding: '6px 12px',
              border: isActive ? `2px solid ${palette.textDark}` : '2px solid transparent',
              borderRadius: '4px',
              backgroundColor: isActive ? palette.board : 'transparent',
              color: isActive ? palette.buttonText : palette.textDark,
              cursor: 'pointer',
              fontSize: '13px',
              fontWeight: isActive ? 'bold' : 'normal',
              fontFamily: 'Arial, sans-serif'
            }}
          >
            <span
              style={{
                display: 'inline-block',
                width: '14px',
                height: '14px',
                borderRadius: '50%',
                backgroundColor: p.board,
                border: '1px solid rgba(0,0,0,0.2)',
                flexShrink: 0
              }}
            />
            {p.name}
          </button>
        )
      })}
    </div>
  )
}

