import React from 'react'
import { Game } from './components/Game'

export const App: React.FC = () => {
  return (
    <div style={{ fontFamily: 'Arial, sans-serif' }}>
      <Game />
    </div>
  )
}

export default App

import React, { useState, useEffect } from 'react'
import { initializeGame, move, GameState } from '../game/logic'
import { Tile } from './Tile'

export const Game: React.FC = () => {
  const [state, setState] = useState<GameState>(initializeGame)

  useEffect(() => {
    const handleKeyPress = (e: KeyboardEvent) => {
      if (['ArrowUp', 'ArrowDown', 'ArrowLeft', 'ArrowRight'].includes(e.key)) {
        e.preventDefault()
        const directionMap: Record<string, 'left' | 'right' | 'up' | 'down'> = {
          ArrowLeft: 'left',
          ArrowRight: 'right',
          ArrowUp: 'up',
          ArrowDown: 'down'
        }
        setState((prev) => move(directionMap[e.key], prev))
      }
    }

    window.addEventListener('keydown', handleKeyPress)
    return () => window.removeEventListener('keydown', handleKeyPress)
  }, [])

  const handleNewGame = () => {
    setState(initializeGame())
  }

  return (
    <div
      style={{
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        justifyContent: 'center',
        minHeight: '100vh',
        backgroundColor: '#faf8f3',
        padding: '20px'
      }}
    >
      <h1 style={{ marginBottom: '20px', color: '#776e65' }}>2048</h1>

      <div style={{ marginBottom: '20px', display: 'flex', gap: '40px', alignItems: 'center' }}>
        <div>
          <p style={{ fontSize: '14px', color: '#9f8f7f', margin: 0 }}>Score</p>
          <p style={{ fontSize: '24px', fontWeight: 'bold', color: '#776e65', margin: '5px 0 0 0' }}>
            {state.score}
          </p>
        </div>
        <button
          onClick={handleNewGame}
          style={{
            padding: '10px 20px',
            backgroundColor: '#8f7a66',
            color: '#f9f6f2',
            border: 'none',
            borderRadius: '3px',
            fontSize: '16px',
            cursor: 'pointer'
          }}
        >
          New Game
        </button>
      </div>

      <div
        style={{
          display: 'grid',
          gridTemplateColumns: 'repeat(4, 75px)',
          gridTemplateRows: 'repeat(4, 75px)',
          gap: '10px',
          backgroundColor: '#bbada0',
          padding: '10px',
          borderRadius: '3px',
          width: 'fit-content',
          height: 'fit-content',
          maxWidth: '100%',
          boxSizing: 'border-box' as const,
          overflow: 'hidden'
        }}
      >
        {state.grid.map((row, i) =>
          row.map((value, j) => (
            <Tile key={`${i}-${j}`} value={value} />
          ))
        )}
      </div>

      {state.won && (
        <div
          style={{
            marginTop: '20px',
            padding: '20px',
            backgroundColor: '#edc22e',
            color: '#776e65',
            borderRadius: '3px',
            fontSize: '18px',
            fontWeight: 'bold'
          }}
        >
          🎉 You won! Keep going or start a new game.
        </div>
      )}

      {state.gameOver && (
        <div
          style={{
            marginTop: '20px',
            padding: '20px',
            backgroundColor: '#f67c5f',
            color: '#f9f6f2',
            borderRadius: '3px',
            fontSize: '18px',
            fontWeight: 'bold'
          }}
        >
          Game Over! Start a new game to play again.
        </div>
      )}

      <div style={{ marginTop: '40px', textAlign: 'center', color: '#9f8f7f', fontSize: '14px' }}>
        <p>Use arrow keys to move tiles</p>
        <p>When two tiles with the same number touch, they merge!</p>
      </div>
    </div>
  )
}

import React from 'react'

interface TileProps {
  value: number | null
}

export const Tile: React.FC<TileProps> = ({ value }) => {
  const getBackgroundColor = (val: number | null): string => {
    const colors: Record<number, string> = {
      2: '#eee4da',
      4: '#ede0c8',
      8: '#f2b179',
      16: '#f59563',
      32: '#f67c5f',
      64: '#f65e3b',
      128: '#edcf72',
      256: '#edcc61',
      512: '#edc850',
      1024: '#edc53f',
      2048: '#edc22e'
    }
    return colors[val!] || '#cdc1b4'
  }

  const getTextColor = (val: number | null): string => {
    return val === null || val === 2 || val === 4 ? '#776e65' : '#f9f6f2'
  }

  return (
    <div
      style={{
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
        backgroundColor: getBackgroundColor(value),
        color: getTextColor(value),
        fontSize: value! > 1024 ? '30px' : '40px',
        fontWeight: 'bold',
        borderRadius: '3px',
        width: '75px',
        height: '75px',
        transition: 'background-color 0.15s ease-in-out, color 0.15s ease-in-out'
      }}
    >
      {value}
    </div>
  )
}

